                method : 'POST',
                headers : {'Content-Type' : 'application/json'},
                boady : JSON.stringify(body)
            });
            console.log(query);
        } catch (error) {
            console.log(error);
        }
    };

return(
<form className='container border p-5 mt-5' style={{textAlign : 'left'}} onSubmit={PostDepartment}>
